//     top: 155.3px;
//     z-index: 3;
// `;

//font-family: Poppins;
const StyledParagraph = styled(Paragraph)`
    font-weight: 600;
    font-size: 12px;
    line-height: 18px;
`;

const TimeView = styled(View)`
    position: absolute;
    left: 0%;
    right: 0%;
    top: 100px;
`;

const StyledDataTable = styled(DataTable)`
    display: flex;
    justify-content: center;
    align-content: center;
    color: black;
`;

const IngredientsView = styled(View)`
    display: flex;
    flex-direction: column;
    position: absolute;
    left: 0%;
    right: 0%;
    top: 250px;
    border-bottom-color: lightgrey;
    border-bottom-width: 0.5px;
    padding-bottom: 30px;

`;

//font-family: Poppins;
const RecipeTitle = styled.Text`
    text-align: center;
    font-style: normal;
    font-weight: 600;
    font-size: 18px;
    line-height: 27px;
    color: #000000;
    /* padding: 10px; */
    margin-top: 20px;
`;

const InstructionsView = styled(View)`
    display: flex;
    flex-direction: column;
    position: absolute;
    left: 0%;
    right: 0%;
    top: 550px;

`;

const StyledSectionList = styled(SectionList)`
    padding-top: 2px;
    padding-left: 10px;
